mapper.insert:
SELECT auc_seq.nextval From DUAL
insert into auc(
) values( #{aucNo},#{userId},#{status},#{highestPrice},#{minimumAmout},#{highestUserId},0,0, SYSDATE,#{startDate}, #{endDate},#{deadline}, #{productId})

mapper.update:
update auc
set highestPrice
= #{highestPrice}, participants = #{participants}, status = #{status},
startDate = #{startDate}, endDate = #{endDate}, deadline = #{deadline}
where aucNo = #{aucNo}
